import { ref, onMounted } from 'vue';
import request from "@/utils/request";
import { ElMessageBox } from 'element-plus';
export default {
  __name: 'HomeView',
  setup(__props, {
    expose: __expose
  }) {
    __expose();
    const name = ref('');
    const currentPage = ref(1); // 当前页码
    const pageSize = ref(10); // 每页显示的条数
    const total = ref(50); // 总条数
    const data = ref({
      arr: []
    });
    const visible = ref(false);
    const dialogFormVisible = ref(false);
    const handleSizeChange = newSize => {
      pageSize.value = newSize;
    };
    const handleCurrentChange = newPage => {
      currentPage.value = newPage; // 更新当前页码
    };
    const fetchData = async () => {
      try {
        const response = await request.get('/user/selectAll');
        data.value.arr = response.data;
      } catch (error) {
        console.error('Error fetching data:', error);
      }
    };

    // Function to handle the search
    const handleSearch = async () => {
      if (name.value.trim()) {
        try {
          const response = await request.get(`/user/selectByName/${name.value.trim()}`);
          data.value.arr = response.data;
        } catch (error) {
          console.error('Error fetching data by name:', error);
        }
      } else {
        // If name input is empty, fetch all data
        fetchData();
      }
    };
    const handleEdit = row => {
      console.log('Editing row:', row);
    };
    const handleDelete = row => {
      console.log('Deleting row:', row);
      visible.value = true;
    };
    onMounted(fetchData);
    const __returned__ = {
      name,
      currentPage,
      pageSize,
      total,
      data,
      visible,
      dialogFormVisible,
      handleSizeChange,
      handleCurrentChange,
      fetchData,
      handleSearch,
      handleEdit,
      handleDelete,
      ref,
      onMounted,
      get request() {
        return request;
      },
      get ElMessageBox() {
        return ElMessageBox;
      }
    };
    Object.defineProperty(__returned__, '__isScriptSetup', {
      enumerable: false,
      value: true
    });
    return __returned__;
  }
};
